import React, { useEffect, useState } from 'react';
import { environment } from '../environment.ts';
import { Link } from 'react-router-dom';
import Footer from './Footer.js';
import 'font-awesome/css/font-awesome.min.css';

const MarksheetList = ({ user }) => {
  const [marksheets, setMarksheets] = useState([]);
  const [nameQuery, setNameQuery] = useState('');
  const [rollNoQuery, setRollNoQuery] = useState('');
  const [filteredMarksheets, setFilteredMarksheets] = useState([]);
  const [noResults, setNoResults] = useState(false);
  const [selectedMarksheetId, setSelectedMarksheetId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1); // Current page state
  const [totalPages, setTotalPages] = useState(1); // Total pages state
  const marksheetsPerPage = 5; // Number of marksheets to display per page

  const apiUrl = environment.apiUrl;

  const fetchMarksheets = (page = 1) => {
    fetch(`${apiUrl}/api/marksheet/searchMarksheets?page=${page}`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setMarksheets(data.marksheets); // Assuming API returns { marksheets: [], totalPages: num }
        setFilteredMarksheets(data.marksheets);
        setTotalPages(data.totalPages); // Set total pages from response
        setNoResults(data.marksheets.length === 0);
      })
      .catch(error => {
        console.error('Error fetching marksheets:', error);
      });
  };

  useEffect(() => {
    fetchMarksheets(currentPage); // Fetch marksheets on current page change
  }, [currentPage]);

  const handleSearch = () => {
    let query = new URLSearchParams();
    if (nameQuery.trim() !== '') {
      query.append('name', nameQuery);
    }
    if (rollNoQuery.trim() !== '') {
      query.append('rollNo', rollNoQuery);
    }
    fetch(`${apiUrl}/api/marksheet/searchMarksheets?${query.toString()}&page=${currentPage}`, {
      credentials: 'include',
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setFilteredMarksheets(data.marksheets);
        setTotalPages(data.totalPages); // Update total pages based on search
        setNoResults(data.marksheets.length === 0);
      })
      .catch(error => {
        console.error('Error searching marksheets:', error);
      });
  };

  const handleResetClick = () => {
    setNameQuery('');
    setRollNoQuery('');
    setCurrentPage(1); // Reset to first page
    fetchMarksheets(1); // Fetch all marksheets for the first page
  };

  const handleDeleteClick = (marksheetId) => {
    setSelectedMarksheetId(marksheetId);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    if (selectedMarksheetId) {
      fetch(`${apiUrl}/api/marksheet/deleteMarksheet/${selectedMarksheetId}`, {
        method: 'POST',
        credentials: 'include',
      })
        .then(response => {
          if (response.ok) {
            fetchMarksheets(currentPage); // Refresh marksheet list after deletion
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .catch(error => {
          console.error('Error deleting marksheet:', error);
        })
        .finally(() => {
          setShowModal(false);
          setSelectedMarksheetId(null);
        });
    }
  };

  // Pagination Controls
  const handleNext = () => {
    if (currentPage < totalPages) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPage > 1) {
      setCurrentPage(prev => prev - 1);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://www.pixelstalk.net/wp-content/uploads/image10/Serene-beach-with-smooth-rocks-gentle-waves-colorful-sunset-1080p-HD-Wallpapers.jpg')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        height: '100vh',
        paddingTop: '85px',
      }}
    >
      <h2 align="center">Marksheet List</h2>

      {/* Search Fields */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          value={nameQuery}
          onChange={(e) => setNameQuery(e.target.value)}
          placeholder="Search by Name"
          style={{ padding: '5px', marginRight: '10px' }}
        />
        <input
          type="text"
          value={rollNoQuery}
          onChange={(e) => setRollNoQuery(e.target.value)}
          placeholder="Search by RollNo"
          style={{ padding: '5px', marginRight: '10px' }}
        />
        <button onClick={handleSearch} className="btn btn-success btn-lg" style={{ marginLeft: '10px', padding: '1px', marginRight: '10px' }}>Search</button>
        <button onClick={handleResetClick} className="btn btn-warning btn-lg" style={{ padding: '1px', marginRight: '10px' }}>
          Reset
        </button>
      </div>

      {noResults ? (
        <h1 style={{ textAlign: 'center', color: 'red' }}>No records found</h1>
      ) : (
        <table border="1px" style={{ borderColor: 'khaki' }} width="100%" align="center" className="table table-hover table-dark table-bordered">
          <thead align="center">
            <tr>
              <th style={{ color: 'skyblue' }}>S.NO</th>
              <th style={{ color: 'skyblue' }}>Name</th>
              <th style={{ color: 'skyblue' }}>Roll No</th>
              <th style={{ color: 'skyblue' }}>Physics</th>
              <th style={{ color: 'skyblue' }}>Chemistry</th>
              <th style={{ color: 'skyblue' }}>Maths</th>
              <th style={{ color: 'skyblue' }}>Total</th>
              <th style={{ color: 'skyblue' }}>Percentage</th>
              <th style={{ color: 'skyblue' }}>Pass/Fail</th>
              <th style={{ color: 'skyblue' }}>Action</th>
            </tr>
          </thead>
          <tbody align="center">
            {filteredMarksheets.slice((currentPage - 1) * marksheetsPerPage, currentPage * marksheetsPerPage).map((marksheet, index) => (
              <tr key={marksheet._id}>
                <td>{(currentPage - 1) * marksheetsPerPage + index + 1}</td>
                <td>{marksheet.name}</td>
                <td>{marksheet.rollNo}</td>
                <td>{marksheet.physics}</td>
                <td>{marksheet.chemistry}</td>
                <td>{marksheet.maths}</td>
                <td>{calculateTotal(marksheet)}</td>
                <td>{calculatePercentage(marksheet).toFixed(2)}%</td>
                <td>{calculatePassFail(marksheet)}</td>
                <td>
                  <button className="btn btn-danger" onClick={() => handleDeleteClick(marksheet._id)} style={{ marginLeft: '10px', padding: '5px' }}><i className="fa fa-trash" aria-hidden="true"></i></button>
                  <Link to={`/editmarksheet/${marksheet._id}`}>
                    <button className="btn btn-primary" style={{ marginLeft: '10px', padding: '5px' }}><i className="fa fa-pencil" aria-hidden="true"></i></button>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination Controls */}
      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
        <button onClick={handlePrevious} disabled={currentPage === 1} className="btn btn-secondary">Previous</button>
        <button onClick={handleNext} disabled={currentPage === totalPages} className="btn btn-secondary">Next</button>
      </div>

      {/* Confirmation Modal */}
      {showModal && (
        <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.7)' }}>
          <div className="modal-content">
            <h4>Confirm Delete</h4>
            <p>Are you sure you want to delete this marksheet?</p>
            <button onClick={handleConfirmDelete} className="btn btn-danger">Delete</button>
            <button onClick={() => setShowModal(false)} className="btn btn-secondary">Cancel</button>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default MarksheetList;
