import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { environment } from '../environment.ts';
import Footer from './Footer';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [page, setPage] = useState(1); // For pagination
  const [totalPages, setTotalPages] = useState(1); // To store total pages

  const apiUrl = environment.apiUrl;
  const limit = 5; // Records per page

  const fetchUsers = (query = '', currentPage = 1) => {
    fetch(`${apiUrl}/api/user/searchuser?query=${query}&page=${currentPage}&limit=${limit}`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setUsers(data.users); // Assuming API returns { users: [], totalPages: X }
        setTotalPages(data.totalPages); // Assuming API returns totalPages
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  useEffect(() => {
    fetchUsers();
  }, [page]); // Refetch users when page changes

  const handleDeleteClick = (userId) => {
    setSelectedUserId(userId);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    if (selectedUserId) {
      fetch(`${apiUrl}/api/user/deleteuser/${selectedUserId}`, {
        method: 'POST',
        credentials: 'include',
      })
        .then(response => {
          if (response.ok) {
            fetchUsers(searchQuery, page); // Refresh current page after delete
          } else if (response.status === 401) {
            throw new Error('Unauthorized');
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
        })
        .finally(() => {
          setShowModal(false);
          setSelectedUserId(null);
        });
    }
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchClick = () => {
    setPage(1); // Reset to first page when searching
    fetchUsers(searchQuery, 1);
  };

  const handleResetClick = () => {
    setSearchQuery(''); // Clear the search query
    fetchUsers(); // Fetch the default list of users without any search query
  };

  const handleNext = () => {
    if (page < totalPages) setPage(page + 1);
  };

  const handlePrevious = () => {
    if (page > 1) setPage(page - 1);
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://img.lovepik.com/photo/48013/0603.jpg_wh860.jpg')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        minHeight: '100vh',
        paddingTop: '85px',
        paddingBottom: '100px'
      }}
    >
      <h1 align="center" style={{ color: 'white' }}>User List</h1>
      <div align="center" style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by first name..."
          value={searchQuery}
          onChange={handleSearchChange}
          style={{ marginRight: '10px', padding: '10px' }}
        />
        <button onClick={handleSearchClick} style={{ padding: '10px' }} className="btn btn-warning">
          Search
        </button>
      </div>

      <table border="1px" style={{ borderColor: 'snow' }} width="100%" align="center" className="table table-hover table-dark table-bordered">
        <thead align="center">
          <tr>
            <th style={{ color: 'skyblue' }}>First Name</th>
            <th style={{ color: 'skyblue' }}>Last Name</th>
            <th style={{ color: 'skyblue' }}>Login ID</th>
            <th style={{ color: 'skyblue' }}>DOB</th>
            <th style={{ color: 'skyblue' }}>Gender</th>
            <th style={{ color: 'skyblue' }}>Role</th>
            <th style={{ color: 'skyblue' }}>Action</th>
          </tr>
        </thead>
        <tbody align="center">
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.loginId}</td>
              <td>{new Date(user.dob).toLocaleDateString()}</td>
              <td>{user.gender}</td>
              <td>{user.role}</td>
              <td>
                <button className="btn btn-danger" onClick={() => handleDeleteClick(user._id)}>Delete</button>
                <Link to={`/edituser/${user._id}`}>
                  <button className="btn btn-primary" style={{ marginLeft: '10px' }}>Edit</button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div align="center" style={{ marginTop: '20px' }}>
        <button className="btn btn-warning" onClick={handlePrevious} disabled={page === 1}>Previous</button>
        <span style={{ color: 'white', margin: '0 15px' }}>Page {page} of {totalPages}</span>
        <button className="btn btn-warning" onClick={handleNext} disabled={page === totalPages}>Next</button>
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '10px' }}>
        <Link to="/adduser">
          <button style={{ padding: '10px' }} className="btn btn-danger">
            New
          </button>
        </Link>
        <button onClick={handleResetClick} style={{ padding: '10px' }} className="btn btn-warning">
          Reset
        </button>
      </div>

      {showModal && (
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Delete</h5>
                <button type="button" className="close" aria-label="Close" onClick={() => setShowModal(false)}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete this user?
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                <button type="button" className="btn btn-danger" onClick={handleConfirmDelete}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default UserList;
