import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { environment } from '../environment.ts';
import Footer from './Footer.js';

const StudentList = () => {
    const [students, setStudents] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedStudentId, setSelectedStudentId] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const apiUrl = environment.apiUrl;

    const fetchStudent = (query = '') => {
        fetch(`${apiUrl}/api/student/searchstudent?query=${query}`, { credentials: 'include' })
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error('Network response was not ok.');
            })
            .then(data => {
                setStudents(data);
            })
            .catch(error => {
                console.error('Error fetching users:', error);
            });
    };

    useEffect(() => {
        fetchStudent();
    }, []);

    const handleDeleteClick = (userId) => {
        setSelectedStudentId(userId);
        setShowModal(true);
    };

    const handleConfirmDelete = () => {
        if (selectedStudentId) {
            fetch(`${apiUrl}/api/student/deletestudent/${selectedStudentId}`, {
                method: 'POST',
                credentials: 'include', // Include credentials for session management
            })
                .then(response => {
                    if (response.ok) {
                        fetchStudent(searchQuery);
                    } else if (response.status === 401) {
                        throw new Error('Unauthorized');
                    } else {
                        throw new Error('Network response was not ok.');
                    }
                })
                .catch(error => {
                    console.error('Error deleting student:', error);
                })
                .finally(() => {
                    setShowModal(false);
                    setSelectedStudentId(null);
                });
        }
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);


    };

    const handleSearchClick = () => {
        fetchStudent(searchQuery);

    };

    const handleResetClick = () => {
        setSearchQuery(''); // Clear the search query
        fetchStudent(); // Fetch the default list of students without any search query
    };

    return (
        <div
            style={{
                backgroundImage: `url('https://www.pixelstalk.net/wp-content/uploads/image10/Serene-beach-with-smooth-rocks-gentle-waves-colorful-sunset-1080p-HD-Wallpapers.jpg')`,
                backgroundRepeat: 'no-repeat',
                backgroundAttachment: 'fixed',
                backgroundSize: 'cover',
                height: '100vh', // Full viewport height
                paddingTop: '85px' // Adjust as needed for top padding
            }}
        >
            <h2 align="center">Student List</h2>
            <div align="center" style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    placeholder="Search by Student name..."
                    value={searchQuery}
                    onChange={handleSearchChange}
                    style={{ marginRight: '10px', padding: '5px' }}
                />
                <input
                    type="text"
                    placeholder="Search by Subject name..."
                    value={searchQuery}
                    onChange={handleSearchChange}
                    style={{ marginRight: '10px', padding: '5px' }}
                />
                <button onClick={handleSearchClick} className="btn btn-success btn-lg" style={{ padding: '1px' }} >
                    Search
                </button>
            </div>

            <table border="1px" style={{ borderColor: 'khaki' }} width="100%" align="center" className="table table-hover table-dark table-bordered">
                <thead align="center">
                    <tr>
                        <th style={{ color: 'skyblue' }}>S.NO</th>
                        <th style={{ color: 'skyblue' }}>Student Name</th>
                        <th style={{ color: 'skyblue' }}>Subject</th>
                        <th style={{ color: 'skyblue' }}>School</th>
                        <th style={{ color: 'skyblue' }}>DOB</th>
                        <th style={{ color: 'skyblue' }}>Mobile No</th>
                        <th style={{ color: 'skyblue' }}>Gender</th>
                        <th style={{ color: 'skyblue' }}>Action</th>
                    </tr>
                </thead>
                <tbody align="center">
                    {students.map((student, index) => (
                        <tr key={student._id}>
                            <td>{index + 1}</td>
                            <td>{student.name}</td>
                            <td>{student.subject}</td>
                            <td>{student.school}</td>
                            <td>{new Date(student.dob).toLocaleDateString()}</td>
                            <td>{student.mobileNo}</td>
                            <td>{student.gender}</td>
                            <td>
                                <Link to={`/editstudent/${student._id}`}>
                                    <button className="btn btn-primary" style={{ marginRight: '10px', padding: '5px' }}>Edit</button>
                                </Link>
                                <button className="btn btn-danger" onClick={() => handleDeleteClick(student._id)} style={{ marginRight: '10px', padding: '5px' }}>Delete</button>

                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Button Section for "New" and "Reset" */}
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '3px', marginTop: '3px' }}>
                <Link to="/addstudent">
                    <button className="btn btn-danger" style={{ padding: '10px' }}>
                        New
                    </button>
                </Link>
                <button onClick={handleResetClick} className="btn btn-warning" style={{ padding: '5px', marginTop: '5px' }}>
                    Reset
                </button>
            </div>

            {/* Delete Confirmation Modal */}
            {showModal && (
                <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Confirm Delete</h5>
                                <button type="button" className="close" aria-label="Close" onClick={() => setShowModal(false)}>
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                Are you sure you want to delete this student?
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                                <button type="button" className="btn btn-danger" onClick={handleConfirmDelete}>Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <Footer />
        </div>
    );
};

export default StudentList;
