import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { environment } from '../environment.ts';
import Footer from './Footer.js';

const MarksheetForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    rollNo: '',
    physics: '',
    chemistry: '',
    maths: ''
  });
  const [studentNames, setStudentNames] = useState([]);
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({}); // To store validation errors

  const apiUrl = environment.apiUrl;

  useEffect(() => {
    // Fetch student names from the server
    fetch(`${apiUrl}/api/student/prelod`, { credentials: 'include' })
      .then(response => response.json())
      .then(data => {
        setStudentNames(data.map(student => student.name));
      })
      .catch(error => console.error('Error fetching student names:', error));

    if (id) {
      fetch(`${apiUrl}/api/marksheet/getMarksheet/${id}`, { credentials: 'include' })
        .then(response => {
          if (response.ok) {
            return response.json();
          } else if (response.status === 401) {
            throw new Error('Unauthorized');
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .then(data => {
          setFormData(data);
        })
        .catch(error => {
          console.error('Error fetching user:', error);
        });
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    setErrors({ ...errors, [name]: '' }); // Clear the error for the changed field
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name) newErrors.name = 'Please select a name.';
    if (!formData.rollNo) newErrors.rollNo = 'Roll number is required.';
    else if (!/^[a-zA-Z0-9]+$/.test(formData.rollNo)) newErrors.rollNo = 'Roll number must be alphanumeric.';

    // Validate marks
    const marks = { physics: formData.physics, chemistry: formData.chemistry, maths: formData.maths };
    Object.entries(marks).forEach(([subject, mark]) => {
      if (!mark) newErrors[subject] = `${subject.charAt(0).toUpperCase() + subject.slice(1)} marks are required.`;
      else if (isNaN(mark) || mark < 0 || mark > 100) newErrors[subject] = `${subject.charAt(0).toUpperCase() + subject.slice(1)} must be a number between 0 and 100.`;
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // Returns true if no errors
  };

  const handleSubmit = () => {
    if (!validateForm()) return; // If validation fails, stop the submission

    const url = id ? `${apiUrl}/api/marksheet/updateMarksheet/${id}` : `${apiUrl}/api/marksheet/addMarksheet`;
    const method = 'POST';

    fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData),
      credentials: 'include',
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else if (response.status === 401) {
          throw new Error('Unauthorized');
        } else {
          throw new Error('Network response was not ok.');
        }
      })
      .then((data) => {
        setFormData({
          name: '',
          rollNo: '',
          physics: '',
          chemistry: '',
          maths: ''
        });
        setMessage(data.message);
        navigate('/MarksheetList');
      })
      .catch(error => {
        console.error('Error submitting form:', error);
        setMessage(error.message);
      });
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://img.freepik.com/premium-photo/stylish-cover-dark-colors-theme-websites-website-development-logos-posts-more-socia_343960-112437.jpg?w=1380')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-4"></div>
          <div className="col-md-4">
            <div className="card" style={{ background: 'linear-gradient(to right, rgba(0, 198, 255, 0.8), rgba(0, 114, 255, 0.8))' }}>
              <div className="card-body">
                <h2 className="card-title text-center">{id ? 'Edit Marksheet' : 'Add Marksheet'}</h2>
                {message && <div className="alert alert-info">{message}</div>}
                <form>
                  <div className="form-group">
                    <label>Name</label>
                    <select
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                    >
                      <option value="">-------Select Name-------</option>
                      {studentNames.map((name, index) => (
                        <option key={index} value={name}>
                          {name}
                        </option>
                      ))}
                    </select>
                    {errors.name && <div className="invalid-feedback">{errors.name}</div>}
                  </div>
                  <div className="form-group">
                    <label>Roll No</label>
                    <input type="text" name="rollNo" value={formData.rollNo} onChange={handleChange} placeholder="Enter Roll No" className={`form-control ${errors.rollNo ? 'is-invalid' : ''}`} />
                    {errors.rollNo && <div className="invalid-feedback">{errors.rollNo}</div>}
                  </div>
                  <div className="form-group">
                    <label>Physics</label>
                    <input type="text" name="physics" value={formData.physics} onChange={handleChange} placeholder="Enter Physics Marks" className={`form-control ${errors.physics ? 'is-invalid' : ''}`} />
                    {errors.physics && <div className="invalid-feedback">{errors.physics}</div>}
                  </div>
                  <div className="form-group">
                    <label>Chemistry</label>
                    <input type="text" name="chemistry" value={formData.chemistry} onChange={handleChange} placeholder="Enter Chemistry Marks" className={`form-control ${errors.chemistry ? 'is-invalid' : ''}`} />
                    {errors.chemistry && <div className="invalid-feedback">{errors.chemistry}</div>}
                  </div>
                  <div className="form-group">
                    <label>Maths</label>
                    <input type="text" name="maths" value={formData.maths} onChange={handleChange} placeholder="Enter Maths Marks" className={`form-control ${errors.maths ? 'is-invalid' : ''}`} />
                    {errors.maths && <div className="invalid-feedback">{errors.maths}</div>}
                  </div>
                  <div className="text-center">
                    <button type="button" onClick={handleSubmit} style={{ padding: '5px' }} className="btn btn-warning">Submit</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
          <div className="col-md-4"></div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default MarksheetForm;
