import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { environment } from '../environment.ts';
import Footer from './Footer';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState({ firstName: '', lastName: '', loginId: '' });
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const apiUrl = environment.apiUrl;

  const fetchUsers = (query = {}) => {
    const filteredQuery = {};
    Object.keys(query).forEach(key => {
      if (query[key]) {
        filteredQuery[key] = query[key];
      }
    });

    const queryString = new URLSearchParams(filteredQuery).toString();

    fetch(`${apiUrl}/api/user/searchuser?${queryString}`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDeleteClick = (userId) => {
    setSelectedUserId(userId);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    if (selectedUserId) {
      fetch(`${apiUrl}/api/user/deleteuser/${selectedUserId}`, {
        method: 'POST',
        credentials: 'include',
      })
        .then(response => {
          if (response.ok) {
            fetchUsers(searchQuery);
          } else if (response.status === 401) {
            throw new Error('Unauthorized');
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
        })
        .finally(() => {
          setShowModal(false);
          setSelectedUserId(null);
        });
    }
  };

  const handleSearchChange = (event) => {
    const { name, value } = event.target;
    setSearchQuery(prev => ({ ...prev, [name]: value }));
  };

  const handleSearchClick = () => {
    fetchUsers(searchQuery);
  };

  const handleResetClick = () => {
    setSearchQuery({ firstName: '', lastName: '', loginId: '', role: '' });
    fetchUsers();
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://www.pixelstalk.net/wp-content/uploads/image10/Serene-beach-with-smooth-rocks-gentle-waves-colorful-sunset-1080p-HD-Wallpapers.jpg')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        minHeight: '100vh',
        paddingTop: '75px',
        paddingBottom: '100px'
      }}
    >
      <h1 align="center" style={{ color: 'black' }}>User List</h1>
      <div align="center" style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="firstName"
          placeholder="Search by first name..."
          value={searchQuery.firstName}
          onChange={handleSearchChange}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          name="role"
          placeholder="Search by Role..."
          value={searchQuery.role}
          onChange={handleSearchChange}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Search by last name..."
          value={searchQuery.lastName}
          onChange={handleSearchChange}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="text"
          name="loginId"
          placeholder="Search by login ID..."
          value={searchQuery.loginId}
          onChange={handleSearchChange}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <button onClick={handleSearchClick} style={{ padding: '7px' }} className="btn btn-success btn-sm">
          Search
        </button>

      </div>



      <table border="1px" style={{ borderColor: 'khaki' }} width="100%" align="center" className="table table-hover table-dark table-bordered">
        <thead align="center">
          <tr>
            <th style={{ color: 'skyblue' }}>First Name</th>
            <th style={{ color: 'skyblue' }}>Last Name</th>
            <th style={{ color: 'skyblue' }}>Login ID</th>
            <th style={{ color: 'skyblue' }}>DOB</th>
            <th style={{ color: 'skyblue' }}>Gender</th>
            <th style={{ color: 'skyblue' }}>Role</th>
            <th style={{ color: 'skyblue' }}>Action</th>
          </tr>
        </thead>
        <tbody align="center">
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.loginId}</td>
              <td>{new Date(user.dob).toLocaleDateString()}</td>
              <td>{user.gender}</td>
              <td>{user.role}</td>
              <td>
                <Link to={`/updateuser/${user._id}`} className="btn btn-primary btn-sm" style={{ marginRight: '5px' }}>Edit</Link>
                <button onClick={() => handleDeleteClick(user._id)} className="btn btn-danger btn-sm">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '1px', marginTop: '5px' }}>
        <Link to="/addUser">
          <button style={{ marginRight: '10px', padding: '5px' }} className="btn btn-danger">New</button>
        </Link>
        <button onClick={handleResetClick} style={{ padding: '5px', marginLeft: 'auto', marginTop: '-1px', pedding: '5px' }} className="btn btn-warning">
          Reset
        </button>
      </div>
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Are you sure you want to delete this user?</h2>
            <button onClick={handleConfirmDelete} className="btn btn-danger">Confirm</button>
            <button onClick={() => setShowModal(false)} className="btn btn-secondary">Cancel</button>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default UserList;
