import React, { useEffect, useState } from 'react';
import { environment } from '../environment.ts';
import { Link } from 'react-router-dom';
import Footer from './Footer.js';

const MarksheetList = ({ user }) => {
  const [marksheets, setMarksheets] = useState([]);
  const [nameQuery, setNameQuery] = useState('');
  const [rollNoQuery, setRollNoQuery] = useState('');
  const [filteredMarksheets, setFilteredMarksheets] = useState([]);
  const [selectedMarksheetId, setSelectedMarksheetId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const apiUrl = environment.apiUrl;

  const fetchMarksheets = () => {
    fetch(`${apiUrl}/api/marksheet/searchMarksheets`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setMarksheets(data);
        setFilteredMarksheets(data);
      })
      .catch(error => {
        console.error('Error fetching marksheets:', error);
      });
  };

  useEffect(() => {
    fetchMarksheets();
  }, []);

  const handleSearch = () => {
    let query = new URLSearchParams();
    if (nameQuery.trim() !== '') {
      query.append('name', nameQuery);
    }
    if (rollNoQuery.trim() !== '') {
      query.append('rollNo', rollNoQuery);
    }
    fetch(`${apiUrl}/api/marksheet/searchMarksheets?${query.toString()}`, {
      credentials: 'include',
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setFilteredMarksheets(data);
      })
      .catch(error => {
        console.error('Error searching marksheets:', error);
      });
  };

  const handleDeleteClick = (marksheetId) => {
    setSelectedMarksheetId(marksheetId);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    if (selectedMarksheetId) {
      fetch(`${apiUrl}/api/marksheet/deleteMarksheet/${selectedMarksheetId}`, {
        method: 'POST', // Adjust the method based on your API
        credentials: 'include',
      })
        .then(response => {
          if (response.ok) {
            fetchMarksheets(); // Refresh the marksheet list
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .catch(error => {
          console.error('Error deleting marksheet:', error);
        })
        .finally(() => {
          setShowModal(false);
          setSelectedMarksheetId(null);
        });
    }
  };

  const calculateTotal = (marksheet) => {
    return marksheet.physics + marksheet.chemistry + marksheet.maths;
  };

  const calculatePercentage = (marksheet) => {
    const total = calculateTotal(marksheet);
    return (total / 300) * 100;
  };

  const calculatePassFail = (marksheet) => {
    return marksheet.physics >= 33 && marksheet.chemistry >= 33 && marksheet.maths >= 33 ? 'Pass' : 'Fail';
  };

  const handleResetClick = () => {
    setNameQuery('');
    setRollNoQuery('');
    fetchMarksheets();
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1464983953574-0892a716854b?q=80&w=870&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        height: '100vh',
        paddingTop: '85px',
      }}
    >
      <h2 align="center">Marksheet List</h2>

      {/* Search Fields */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          value={nameQuery}
          onChange={(e) => setNameQuery(e.target.value)}
          placeholder="Search by Name"
          style={{ padding: '5px', width: '200px', marginRight: '10px' }}
        />
        <button onClick={handleSearch} className="btn btn-success btn-lg" style={{ padding: '5px 10px' }}>Search</button>
      </div>

      <table border="1px" width="100%" align="center" style={{ borderColor: 'skyblue' }} className="table table-hover table-dark table-bordered">
        <thead align="center">
          <tr>
            <th>Name</th>
            <th>Roll No</th>
            <th>Physics</th>
            <th>Chemistry</th>
            <th>Maths</th>
            <th>Total</th>
            <th>Percentage</th>
            <th>Pass/Fail</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody align="center">
          {filteredMarksheets.map(marksheet => (
            <tr key={marksheet._id}>
              <td>{marksheet.name}</td>
              <td>{marksheet.rollNo}</td>
              <td>{marksheet.physics}</td>
              <td>{marksheet.chemistry}</td>
              <td>{marksheet.maths}</td>
              <td>{calculateTotal(marksheet)}</td>
              <td>{calculatePercentage(marksheet).toFixed(2)}%</td>
              <td>{calculatePassFail(marksheet)}</td>
              <td>
                <Link to={`/editmarksheet/${marksheet._id}`}>
                  <button className="btn btn-primary" style={{ marginLeft: '10px', pedding: '5px' }}>Edit</button>
                </Link>
                <button className="btn btn-danger" onClick={() => handleDeleteClick(marksheet._id)} style={{ marginLeft: '10px', pedding: '5px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Buttons for New and Reset */}
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '1px', marginTop: '-17px' }}>
        <Link to="/addMarksheet">
          <button style={{ padding: '5x' }} className="btn btn-danger">New</button>
        </Link>
        <button onClick={handleResetClick} style={{ padding: '10px', marginLeft: 'auto', marginTop: '-1px', pedding: '5px' }} className="btn btn-warning">
          Reset
        </button>
      </div>

      {showModal && (
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Delete</h5>
                <button type="button" className="close" aria-label="Close" onClick={() => setShowModal(false)}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete this marksheet?
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                <button type="button" className="btn btn-danger" onClick={handleConfirmDelete}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default MarksheetList;
