import React, { useEffect, useState } from 'react';
import { environment } from '../environment.ts';

const MarksheetList = ({ user }) => {
  const [marksheets, setMarksheets] = useState([]);
  const [nameQuery, setNameQuery] = useState(''); // State for searching by name
  const [rollNoQuery, setRollNoQuery] = useState(''); // State for searching by roll number
  const [filteredMarksheets, setFilteredMarksheets] = useState([]);

  const apiUrl = environment.apiUrl;

  const fetchMarksheets = () => {
    fetch(`${apiUrl}/api/marksheet/searchMarksheets`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setMarksheets(data);
        setFilteredMarksheets(data); // Initially display all marksheets
      })
      .catch(error => {
        console.error('Error fetching marksheets:', error);
      });
  };

  useEffect(() => {
    fetchMarksheets();
  }, []);

  // Combined search functionality for both Name and Roll No
  const handleSearch = () => {
    let query = new URLSearchParams(); // Use URLSearchParams to build the query string

    if (nameQuery.trim() !== '') {
      query.append('name', nameQuery);
    }

    if (rollNoQuery.trim() !== '') {
      query.append('rollNo', rollNoQuery);
    }

    // Fetch filtered marksheets based on search criteria
    fetch(`${apiUrl}/api/marksheet/searchMarksheets?${query.toString()}`, {
      credentials: 'include',
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setFilteredMarksheets(data); // Set the filtered results
      })
      .catch(error => {
        console.error('Error searching marksheets:', error);
      });
  };

  const calculateTotal = (marksheet) => {
    return marksheet.physics + marksheet.chemistry + marksheet.maths;
  };

  const calculatePercentage = (marksheet) => {
    const total = calculateTotal(marksheet);
    return (total / 300) * 100; // Assuming each subject is out of 100
  };

  const calculatePassFail = (marksheet) => {
    return marksheet.physics >= 33 && marksheet.chemistry >= 33 && marksheet.maths >= 33 ? 'Pass' : 'Fail';
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSoAlN-mE7lw_OtWkbMI6xNU3qhLpD1UsniwQ&s')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        height: '100vh', // Full viewport height
        paddingTop: '85px' // Adjust as needed for top padding
      }}

    >
      <h2 align="center">Marksheet List</h2>

      {/* Search Fields */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          value={nameQuery}
          onChange={(e) => setNameQuery(e.target.value)}
          placeholder="Search by Name"
          style={{ padding: '5px', width: '200px', marginRight: '10px' }}
        />
        {/* <input
          type="text"
          value={rollNoQuery}
          onChange={(e) => setRollNoQuery(e.target.value)}
          placeholder="Search by Roll No"
          style={{ padding: '5px', width: '200px', marginLeft: '20px', marginRight: '10px' }}
        />/ */}
        <button onClick={handleSearch} className="btn btn-success btn-lg" style={{ padding: '5px 10px' }}>Search</button>
      </div>

      <table border="1px" width="100%" align="center" style={{ borderColor: 'skyblue' }} className="table table-hover table-dark table-bordered">
        <thead align="center">
          <tr>
            <th>Name</th>
            <th>Roll No</th>
            <th>Physics</th>
            <th>Chemistry</th>
            <th>Maths</th>
            <th>Total</th>
            <th>Percentage</th>
            <th>Pass/Fail</th>
          </tr>
        </thead>
        <tbody align="center">
          {filteredMarksheets.map(marksheet => (
            <tr key={marksheet._id}>
              <td>{marksheet.name}</td>
              <td>{marksheet.rollNo}</td>
              <td>{marksheet.physics}</td>
              <td>{marksheet.chemistry}</td>
              <td>{marksheet.maths}</td>
              <td>{calculateTotal(marksheet)}</td>
              <td>{calculatePercentage(marksheet).toFixed(2)}%</td>
              <td>{calculatePassFail(marksheet)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MarksheetList;
