import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { environment } from '../environment.ts';
import Footer from './Footer';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [firstNameSearchQuery, setFirstNameSearchQuery] = useState('');
  const [loginIdSearchQuery, setLoginIdSearchQuery] = useState('');
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const apiUrl = environment.apiUrl;

  const fetchUsers = (firstNameQuery = '', loginIdQuery = '') => {
    fetch(`${apiUrl}/api/user/searchuser?firstName=${firstNameQuery}&loginId=${loginIdQuery}`, { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Network response was not ok.');
      })
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDeleteClick = (userId) => {
    setSelectedUserId(userId);
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    if (selectedUserId) {
      fetch(`${apiUrl}/api/user/deleteuser/${selectedUserId}`, {
        method: 'POST',
        credentials: 'include',
      })
        .then(response => {
          if (response.ok) {
            fetchUsers(firstNameSearchQuery, loginIdSearchQuery);
          } else if (response.status === 401) {
            throw new Error('Unauthorized');
          } else {
            throw new Error('Network response was not ok.');
          }
        })
        .catch(error => {
          console.error('Error deleting user:', error);
        })
        .finally(() => {
          setShowModal(false);
          setSelectedUserId(null);
        });
    }
  };

  const handleFirstNameSearchChange = (event) => {
    setFirstNameSearchQuery(event.target.value);
  };

  const handleLoginIdSearchChange = (event) => {
    setLoginIdSearchQuery(event.target.value);
  };

  const handleSearchClick = () => {
    fetchUsers(firstNameSearchQuery, loginIdSearchQuery);
  };

  const handleResetClick = () => {
    setFirstNameSearchQuery(''); // Clear the first name search query
    setLoginIdSearchQuery(''); // Clear the login ID search query
    fetchUsers(); // Fetch the default list of users without any search query
  };

  return (
    <div
      style={{
        backgroundImage: `url('https://img.lovepik.com/photo/48013/0603.jpg_wh860.jpg')`,
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        backgroundSize: 'cover',
        minHeight: '100vh',
        paddingTop: '85px',
        paddingBottom: '100px'
      }}
    >
      <h1 align="center" style={{ color: 'white' }}>User List</h1>
      <div align="center" style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by first name..."
          value={firstNameSearchQuery}
          onChange={handleFirstNameSearchChange}
          style={{ marginRight: '10px', padding: '10px' }}
        />
        <input
          type="text"
          placeholder="Search by login ID..."
          value={loginIdSearchQuery}
          onChange={handleLoginIdSearchChange}
          style={{ marginRight: '10px', padding: '10px' }}
        />
        <button onClick={handleSearchClick} style={{ padding: '10px' }} className="btn btn-warning">
          Search
        </button>
      </div>

      <table border="1px" style={{ borderColor: 'snow' }} width="100%" align="center" className="table table-hover table-dark table-bordered">
        <thead align="center">
          <tr>
            <th style={{ color: 'skyblue' }}>First Name</th>
            <th style={{ color: 'skyblue' }}>Last Name</th>
            <th style={{ color: 'skyblue' }}>Login ID</th>
            <th style={{ color: 'skyblue' }}>DOB</th>
            <th style={{ color: 'skyblue' }}>Gender</th>
            <th style={{ color: 'skyblue' }}>Role</th>
            <th style={{ color: 'skyblue' }}>Action</th>
          </tr>
        </thead>
        <tbody align="center">
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.loginId}</td>
              <td>{new Date(user.dob).toLocaleDateString()}</td>
              <td>{user.gender}</td>
              <td>{user.role}</td>
              <td>
                <button className="btn btn-danger" onClick={() => handleDeleteClick(user._id)}>Delete</button>
                <Link to={`/edituser/${user._id}`}>
                  <button className="btn btn-primary" style={{ marginLeft: '10px' }}>Edit</button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '10px' }}>
        <Link to="/adduser">
          <button style={{ padding: '10px' }} className="btn btn-danger">
            New
          </button>
        </Link>
        <button onClick={handleResetClick} style={{ padding: '10px' }} className="btn btn-warning">
          Reset
        </button>
      </div>

      {showModal && (
        <div className="modal fade show" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Confirm Delete</h5>
                <button type="button" className="close" aria-label="Close" onClick={() => setShowModal(false)}>
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete this user?
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                <button type="button" className="btn btn-danger" onClick={handleConfirmDelete}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default UserList;
